import React, { Component } from 'react';
import './App.css';

import SpotifyWebApi from 'spotify-web-api-js';
import { importDefaultSpecifier } from '@babel/types';
//import { conditionalExpression } from '@babel/types';
var spotifyApi = new SpotifyWebApi();

class App extends Component {
  constructor(){
    super();
    var params = this.getHashParams();
    var token = params.access_token;
    if (token) {
      spotifyApi.setAccessToken(token);
    }
    this.state = {
      loggedIn: token ? true : false,
      images: false
    }

  }
  getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    e = r.exec(q)
    while (e) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
       e = r.exec(q);
    }
    return hashParams;
  }


  getTopArtists=()=>{
    spotifyApi.getMyTopArtists()
    .then((response)=>{ 
      this.setState({
          images: response.items.map(function (artist) { 
      return {"Artist": artist.name, "Image": artist.images[0].url, "Link": artist.external_urls.spotify}
       }) 
    })
  })
  console.log(this.state.images)
  }

getRelatedArtists=(ArtistID)=>{
  spotifyApi.getArtistRelatedArtists(ArtistID)
  .then((response)=>{ 
    this.setState({
        images: response.items.map(function (artist) { 
    return {"Artist": artist.name, "Image": artist.images[0].url, "ID": artist.id}
     }) 
  })
})

}



  render() {
    return (
      <div className="App">

        <a href='http://localhost:8888' > 
        <button>Login To Spotify </button>
        </a>

        <div class="containerParent">
      {this.state.images && this.state.images.map((img) => (
        <div class="container">

        < img className="circular--portrait" alt={img.Artist} src = {img.Image} onClick={()=>this.getRelatedArtists(img.ID)} />
        <div class="centered">{img.Artist}</div>

         </div>


          ))}
        </div>
        { this.state.loggedIn &&
          <button onClick={() => this.getTopArtists()}>
            Get Top Artists
          </button>
        }
      </div>
    );
  }
}

export default App;
